package program;

public class Concatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 100;
		int j = 600;
		String s1 = "selenim";
		String s2 = "Java";
		System.out.println(s1+s2);
		System.out.println(i+j);
		System.out.println(s1+s2+i+j);
		System.out.println(s1+s2+(i+j));
	}

}
